class Element:
    def __init__(self, name, symbol, atomic_number, atomic_weight, group, period, group_block, block, state, year_discovered, inventor):
        self.name = name
        self.symbol = symbol
        self.atomic_number = atomic_number
        self.atomic_weight = atomic_weight
        self.group = group
        self.period = period
        self.group_block = group_block
        self.block = block
        self.state = state
        self.year_discovered = year_discovered
        self.inventor = inventor

